import { NumberHelpers } from './number.helper';

export const CalculationsHelper = {
  cost: (clientCpm, impressions) => (NumberHelpers.areNumbers(clientCpm, impressions) ? (impressions / 1000) * clientCpm : null),
  cpm: (spend, impressions) => (NumberHelpers.divide(spend, impressions) !== null ? NumberHelpers.divide(spend, impressions) * 1000 : null),
  cpc: (spend, clicks) => NumberHelpers.divide(spend, clicks),
  ctr: (clicks, impressions) => NumberHelpers.percent(NumberHelpers.divide(clicks, impressions)),
  ctc: (clicks, conversions) => NumberHelpers.divide(clicks, conversions),
  cpa: (spend, conversions) => NumberHelpers.divide(spend, conversions),
};
