export const NumberHelpers = {
  areNumbers: (...args) => (args && args.length > 0 ? !args.map(a => a && !isNaN(a)).some(a => a === undefined || a === false || a === null) : false),
  divide: (numerator, denominator) => (NumberHelpers.areNumbers(numerator, denominator) ? (denominator !== 0 ? numerator / denominator : 0) : null),
  percent: (num) => NumberHelpers.areNumbers(num) ? num * 100 : null,
};
