import chai from 'chai';
import { CalculationsHelper } from '../../src/helpers';

const should = chai.should();

describe('CalculationsHelper', () => {
  describe('cpc', () => {
    it('should return null when spend and clicks is not provided', () => {
      const result = CalculationsHelper.cpc();
      should.equal(result, null);
    });
    it('should return null when clicks is not provided', () => {
      const result = CalculationsHelper.cpc(1000);
      should.equal(result, null);
    });
    it('should return null when spend is not valid', () => {
      const result = CalculationsHelper.cpc('asd', 100);
      should.equal(result, null);
    });
    it('should return null when spend is undefined', () => {
      const result = CalculationsHelper.cpc(undefined, 100);
      should.equal(result, null);
    });
    it('should return null when spend is null', () => {
      const result = CalculationsHelper.cpc(null, 100);
      should.equal(result, null);
    });
    it('should return null when clicks is not valid', () => {
      const result = CalculationsHelper.cpc(1000, 'asd');
      should.equal(result, null);
    });
    it('should return result when spend and clicks are string numbers', () => {
      const result = CalculationsHelper.cpc('1000', '100');
      result.should.be.an('number');
      result.should.equal(10);
    });
    it('should return result when spend and clicks are numbers', () => {
      const result = CalculationsHelper.cpc(1000, 100);
      result.should.be.an('number');
      result.should.equal(10);
    });
    it('should return 0 when clicks is 0', () => {
      const result = CalculationsHelper.cpc(1000, 0);
      result.should.be.an('number');
      result.should.equal(0);
    });
  });
});
