import chai from 'chai';
import { CalculationsHelper } from '../../src/helpers';

const should = chai.should();

describe('CalculationsHelper', () => {
  describe('cpm', () => {
    it('should return null when spend and impressions is not provided', () => {
      const result = CalculationsHelper.cpm();
      should.equal(result, null);
    });
    it('should return null when impressions is not provided', () => {
      const result = CalculationsHelper.cpm(1000);
      should.equal(result, null);
    });
    it('should return null when spend is undefined', () => {
      const result = CalculationsHelper.cpm(undefined, 100);
      should.equal(result, null);
    });
    it('should return null when spend is null', () => {
      const result = CalculationsHelper.cpm(null, 100);
      should.equal(result, null);
    });
    it('should return null when spend is not valid', () => {
      const result = CalculationsHelper.cpm('asd', 100);
      should.equal(result, null);
    });
    it('should return null when impressions is not valid', () => {
      const result = CalculationsHelper.cpm(1000, 'asd');
      should.equal(result, null);
    });
    it('should return result when spend and impressions are string numbers', () => {
      const result = CalculationsHelper.cpm('1000', '100');
      result.should.be.an('number');
      result.should.equal(10000);
    });
    it('should return result when spend and impressions are numbers', () => {
      const result = CalculationsHelper.cpm(1000, 100);
      result.should.be.an('number');
      result.should.equal(10000);
    });
    it('should return 0 when impressions is 0', () => {
      const result = CalculationsHelper.cpm(1000, 0);
      result.should.be.an('number');
      result.should.equal(0);
    });
  });
});
