import chai from 'chai';
import { CalculationsHelper } from '../../src/helpers';

const should = chai.should();

describe('CalculationsHelper', () => {
  describe('ctc', () => {
    it('should return null when clicks and conversions is not provided', () => {
      const result = CalculationsHelper.ctc();
      should.equal(result, null);
    });
    it('should return null when clicks is not provided', () => {
      const result = CalculationsHelper.ctc(1000);
      should.equal(result, null);
    });
    it('should return null when clicks isundefined', () => {
      const result = CalculationsHelper.ctc(undefined, 100);
      should.equal(result, null);
    });
    it('should return null when clicks is null', () => {
      const result = CalculationsHelper.ctc(null, 100);
      should.equal(result, null);
    });
    it('should return null when clicks is not valid', () => {
      const result = CalculationsHelper.ctc('asd', 100);
      should.equal(result, null);
    });
    it('should return null when clicks is not valid', () => {
      const result = CalculationsHelper.ctc(1000, 'asd');
      should.equal(result, null);
    });
    it('should return result when clicks and conversions are string numbers', () => {
      const result = CalculationsHelper.ctc('1000', '100');
      result.should.be.an('number');
      result.should.equal(10);
    });
    it('should return result when clicks and conversions are numbers', () => {
      const result = CalculationsHelper.ctc(1000, 100);
      result.should.be.an('number');
      result.should.equal(10);
    });
    it('should return 0 when conversions is 0', () => {
      const result = CalculationsHelper.ctc(1000, 0);
      result.should.be.an('number');
      result.should.equal(0);
    });
  });
});
